const { input1, input2, operator } = answers;
if (operator === 'square root' && input1) {
    let result = 0;
    result = Math.sqrt(input1);
    console.log(`the square root of  ${input1} is ${result}`);
}
else if (operator === 'cube root' && input1) {
    let result = 0;
    result = Math.cbrt(input1);
    console.log(`cube root of ${input1} is ${result}`);
}
else if (operator === 'square' && input1) {
    let result = 0;
    result = input1 * input1;
    console.log(`square of ${input1} is ${result}`);
}
else if (operator === 'cube' && input1) {
    let result = 0;
    result = input1 * input1 * input1;
    console.log(`cube of ${input1} is ${result}`);
}
else if (input1 && input2 && operator) {
    let result = 0;
    if (operator === '+') {
        result = input1 + input2;
    }
    else if (operator === '-') {
        result = input1 - input2;
    }
    else if (operator === '/') {
        result = input1 / input2;
    }
    else if (operator === '*') {
        result = input1 * input2;
    }
    else if (operator === '^') {
        result = input1 ** input2;
    }
    console.log(`${input1} ${operator} ${input2} is ${result}`);
}
else {
    console.log('valid pls');
}
